mapper.update:
UPDATE approval
SET result=1 , date=#{date}
WHERE document_id=#{documentId} AND employee_id=#{employeeId}

UPDATE approval
SET result=2 , date=#{date} ,comment=#{comment}
WHERE document_id=#{documentId}	AND employee_id=#{employeeId}

		UPDATE document
